onClick:
(() => {
const jsonbData = appsmith.store.selectedVideoAppart; // Remplacez par votre variable

  const formattedText = Object.entries(jsonbData)
    .map(([key, value]) => `${key}: ${value}`)
    .join('\n');
  const add_item = `${formattedText}`;

  const currentValue = appsmith.store.sms_content || "";
  const separator = currentValue ? "\n" : "";
  const newValue = currentValue + separator + add_item;

  storeValue("sms_content", newValue);
  message_input_sms.setValue(newValue); // ‚Üê Widget Multiline Input
})();
